    ''filter[vendorNumber]'': process.env.APP_STORE_VENDOR_NUMBER
  };

  try {
    const responseZ1 = await client.request(''/financeReports'', paramsZ1);
    const contentZ1 = gunzipSync(responseZ1).toString(''utf-8'');
