import dotenv from ''dotenv'';
dotenv.config();

// Test different region codes
const regions = [
  { code: ''WW'', name: ''Worldwide'' },
  { code: ''US'', name: ''United States'' },
  { code: ''EU'', name: ''Europe'' },
  { code: ''JP'', name: ''Japan'' },
  { code: ''AU'', name: ''Australia'' },
  { code: ''CA'', name: ''Canada'' },
  { code: ''ROW'', name: ''Rest of World'' }
];

console.log(''Testing FINANCIAL report region codes...'');
console.log('''');

import(''./src/auth/jwt-manager.js'').then(({ JWTManager }) => {
  import(''./src/api/client.js'').then(({ AppStoreClient }) => {
    import(''zlib'').then(({ gunzipSync }) => {
      const auth = new JWTManager({
        keyId: process.env.APP_STORE_KEY_ID,
        issuerId: process.env.APP_STORE_ISSUER_ID,
        p8Path: process.env.APP_STORE_P8_PATH
      });

      const client = new AppStoreClient(auth);

      Promise.all(regions.map(async ({ code, name }) => {
        const params = {
          ''filter[reportType]'': ''FINANCIAL'',
          ''filter[regionCode]'': code,
          ''filter[reportDate]'': ''2025-10'',
          ''filter[vendorNumber]'': process.env.APP_STORE_VENDOR_NUMBER
        };

        try {
          const response = await client.request(''/financeReports'', params);
          const content = gunzipSync(response).toString(''utf-8'');
          const lines = content.split(''\n'').filter(l => l.trim());
          const headers = lines[0].split(''\t'');
          const extShareIdx = headers.findIndex(h => h === ''Extended Partner Share'');

          let total = 0;
          for (let i = 1; i < lines.length; i++) {
            const values = lines[i].split(''\t'');
            total += parseFloat(values[extShareIdx] || ''0'');
          }

          return { code, name, status: ''SUCCESS'', total, rows: lines.length - 1 };
        } catch (error) {
          return { code, name, status: ''FAILED'', error: error.message };
        }
      })).then(results => {
        console.log(''Region Code Test Results:'');
        console.log(''‚ïê''.repeat(60));

        results.forEach(({ code, name, status, total, rows, error }) => {
          if (status === ''SUCCESS'') {
            console.log(''‚úÖ'', code.padEnd(5), name.padEnd(15), ''Revenue: $'' + total.toFixed(2), ''('' + rows + '' rows)'');
          } else {
            console.log(''‚ùå'', code.padEnd(5), name.padEnd(15), ''Not available'');
          }
        });

        console.log('''');
        console.log(''üí° TIP: Add up all available regions to get worldwide total'');
      });
    });
  });
});
")
